urn"0 B";var a=Math.floor(Math.log(t)/Math.log(1e3));return(t/Math.pow(1e3,a)).toPrecision(3)+" "+"B","KB","MB","GB","TB","PB","EB","ZB","YB"}function updataData(){$.ajax({url:"/api",type:"GET",data:{RnD:Math.random()},dataType:"json",success:function(t){$("#tbu").html(bytesToSize(t.result.totals.bandwidth.all)),$("#trs").html(t.result.totals.requests.all),$("#chr").html(t.result.totals.uniques.all),$("#statb").html(""),traffic=t.result.totals.bandwidth.country,str1="",i=0,Object.keys(traffic).forEach(function(t){i++,str1="trtd"+i+"/tdtd"+t+"/tdtd"+bytesToSize(traffic[t])+"/td/tr",$("#statb").append(str1)})},error:function(){alert("获取失败!")}})}updataData();var count=30;setInterval(function(){count--,$("#upd").html("数据将在 "+count+" 秒 后更新"),0===count&&(count=30,updataData())},1e3)/script/body/html
