        }
    },
    {
        name: "升级文案",
        html: label for="modal"语气词/labelinput type="text" id="modal" class="form-control" placeholder="震惊" requiredlabel for="subject"主体物/labelinput type="text" id="subject" class="form-control" placeholder="香蕉" requiredlabel for="attribute"定语/labelinput type="text" id="attribute" class="form-control" placeholder="尽量避免" requiredlabel for="secSubject"与什么...（第二主体）/labelinput type="text" id="secSubject" class="form-control" placeholder="木瓜" requiredlabel for="event"事件/labelinput type="text" id="event" class="form-control" placeholder="频繁混合使用" required,
