body {
  background-color: #fff;
  -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
  -webkit-font-smoothing: antialiased;
  -webkit-text-size-adjust: 100%;
  height: 100%;
  overflow: hidden;
}
body {
  font-family: apple-system, BlinkMacSystemFont, Segoe UI, Helvetica, Arial, PingFang SC, Microsoft YaHei, sans-serif;
  font-size: 14px;
  line-height: 1;
  color: #333;
  position: relative;
  word-wrap: break-word;
}
body,
h1,
h2,
h3,
h4,
ul,
li,
p {
  margin: 0;
  padding: 0;
  font-weight: normal;
}
button {
  appearance: none;
  cursor: pointer;
  outline: none;
  border: none;
}
a {
  text-decoration: none;
  cursor: pointer;
}
#error {
  width: 100%;
  height: 100%;
  background-color: rgba(255, 255, 255, 0.9);
  position: fixed;
  top: 0;
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
}
#error div {
  background-color: #fff;
  box-shadow: 0 0 3px rgba(0, 0, 0, 0.15);
  border-radius: 3px;
  width: 80%;
  box-sizing: border-box;
  padding: 14px 16px;
}
#error h2 {
  font-size: 20px;
  font-weight: 500;
}
#error p {
  color: #666;
  margin-top: 10px;
  line-height: 1.6;
  white-space: pre-wrap;
}
.page {
  transform: translate3d(0, 0, 0);
  background: #fff;
  position: fixed;
  width: 100%;
  top: 0;
  left: 0;
  height: 100%;
  overflow: hidden;
}
.page.scroll {
  overflow-y: scroll;
  -webkit-overflow-scrolling: touch;
}
.page-current {
  z-index: 1;
}
.page-movefrom {
  animation: movefrom 0.4s ease-in-out both;
}
.page-moveto {
  animation: moveto 0.5s ease-in both;
}
@keyframes movefrom {
  from {
    transform: translate3d(-200px, 0, 0);
  }
}
@keyframes moveto {
  to {
    transform: translate3d(-100%, 0, 0);
  }
}
#user {
  text-align: center;
}
#user > a {
  display: inline-block;
}
#user img {
  padding: 4px;
  border: 1px solid #eaeaea;
  display: inline-block;
  width: 100px;
  height: 100px;
  margin: 70px auto 0;
  border-radius: 50%;
}
@media (max-width: 800px) {
  #user img {
    width: 75px;
    height: 75px;
    margin-top: 40px;
  }
}
#user h1 {
  font-size: 26px;
  margin-top: 20px;
  color: #42505a;
  font-weight: 600;
}
@media (max-width: 800px) {
  #user h1 {
    font-size: 20px;
  }
}
#user p {
  color: #5e656b;
  font-size: 16px;
  line-height: 1.4;
  margin: 14px 20px 0;
}
@media (max-width: 800px) {
  #user p {
    font-size: 14px;
  }
}
#user .social a {
  display: inline-block;
  margin: 20px 8px 30px;
}
@media (max-width: 800px) {
  #user .social a {
    margin: 16px 6px 20px;
  }
}
#user .social a:hover path {
  fill: #363a42;
}
#user svg {
  display: block;
  width: 22px;
  height: 22px;
}
@media (max-width: 800px) {
  #user svg {
    width: 18px;
    height: 18px;
  }
}
#user path {
  fill: #565b65;
}
#user .social:after {
  display: block;
  margin: 0 auto;
  width: 50%;
  content: '';
  height: 1px;
  background: #f1f1f1;
}
.button {
  margin: 50px auto 0;
  display: block;
  text-align: center;
  max-width: 170px;
  border: 2px solid #c3c2c9;
  color: #686868;
  border-radius: 5px;
  font-size: 14px;
  height: 32px;
  background: #fff;
  overflow: hidden;
  position: relative;
}
a.button {
  line-height: 30px;
  height: 30px;
}
#posts {
  text-align: center;
}
#posts > .post {
  text-align: left;
  cursor: pointer;
  position: relative;
  padding: 20px 10px;
  display: block;
}
#posts > .post:first-child {
  margin-top: 50px;
}
@media (max-width: 800px) {
  #posts > .post:first-child {
    margin-top: 30px;
  }
}
#posts > .post:hover {
  background: #f6f7f7;
}
#posts > .post div {
  position: absolute;
  right: 10px;
  top: 23px;
}
@media (max-width: 800px) {
  #posts > .post div {
    display: none;
  }
}
#posts > .post span {
  margin: 0 3px;
  font-size: 13px;
  color: #565b65;
  border: 1px solid #e1e1e1;
  background: #e8e8e8;
  border-radius: 3px;
  padding: 1px 3px 2px;
}
#posts > .button {
  display: inline-block;
  width: 100px;
  text-align: center;
  margin-right: 5px;
  margin-left: 5px;
}
#posts p {
  font-size: 14px;
  color: #788590;
  margin: 14px 0 0 1px;
}
@media (max-width: 800px) {
  #posts p {
    font-size: 13px;
  }
}
#posts h2 {
  color: #485763;
  font-size: 16px;
  line-height: 1.4;
  font-weight: 500;
  width: 60%;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
@media (max-width: 800px) {
  #posts h2 {
    font-size: 15px;
  }
}
@media (max-width: 800px) {
  #posts h2 {
    width: 100%;
  }
}
#post > p {
  font-size: 14px;
  color: #788590;
  margin-top: 10px;
}
#post > p span {
  margin: 0 10px;
  color: #565f67;
}
#post .labels {
  margin-top: 30px;
}
#post .labels a {
  margin-right: 10px;
  text-decoration: underline;
  color: #666;
}
#post > .markdown-body {
  margin-top: 40px;
}
#post > .markdown-body:before {
  content: '';
  width: 30px;
  display: block;
  margin-bottom: 20px;
  height: 1px;
  background: #eee;
}
#post .back {
  position: absolute;
  right: 50%;
  margin-right: 370px;
  top: 72px;
  width: 30px;
  height: 30px;
  cursor: pointer;
}
@media (max-width: 800px) {
  #post .back {
    top: 20px;
    left: 20px;
  }
}
#post .back:hover path {
  fill: #5c646b;
}
#post .back path {
  fill: #949fa9;
}
#post > h1 {
  font-size: 24px;
  font-weight: 500;
  line-height: 1.4;
  color: #3d4348;
  padding-top: 70px;
}
@media (max-width: 800px) {
  #post > h1 {
    font-size: 20px;
  }
}
.comment-list:before {
  display: block;
  width: 30%;
  content: '';
  height: 1px;
  background: #f1f1f1;
  margin: 50px auto;
}
.comment-list li {
  list-style: none;
  margin-top: 20px;
  position: relative;
  padding: 10px 0 10px 70px;
}
@media (max-width: 800px) {
  .comment-list li {
    padding-left: 60px;
  }
}
.comment-list .comment-body {
  border: 1px solid #dee1e2;
  padding: 14px 14px 14px 18px;
  border-radius: 3px;
  position: relative;
}
.comment-list .comment-body > a {
  color: #333;
  text-decoration: none;
  font-weight: 700;
}
.comment-list .comment-body span {
  font-size: 14px;
  color: #767676;
}
.comment-list .comment-body:before {
  content: '';
  position: absolute;
  border: 1px solid transparent;
  border-top-color: #dee1e2;
  border-left-color: #dee1e2;
  width: 5px;
  height: 5px;
  background-color: #fff;
  left: -5px;
  top: 20px;
  transform: rotate(-45deg);
}
.comment-list .markdown-body {
  margin-top: 10px;
  font-size: 14px;
  white-space: pre-wrap;
}
.comment-list .author {
  border: 1px solid #eee;
  position: absolute;
  left: 0;
  top: 10px;
  width: 48px;
  height: 48px;
  border-radius: 50%;
  overflow: hidden;
}
@media (max-width: 800px) {
  .comment-list .author {
    width: 36px;
    height: 36px;
  }
}
.comment-list .author img {
  display: block;
  width: 100%;
  height: auto;
}
@media (max-width: 800px) {
  .comment-body:before {
    top: 16px!important;
  }
}
#footer {
  line-height: 1.4;
  text-align: center;
  font-size: 14px;
  margin-top: 100px;
  color: #5c6e7b;
  padding-bottom: 70px;
}
@media (max-width: 800px) {
  #footer {
    margin-top: 50px;
  }
}
#footer a {
  color: #37444e;
  text-decoration: underline;
}
.page > div {
  margin: 0 auto;
  max-width: 700px;
  padding: 0 20px;
}
.markdown-body {
  font-family: apple-system, BlinkMacSystemFont, Segoe UI, Helvetica, Arial, PingFang SC, Microsoft YaHei, sans-serif;
  font-size: 14px;
}
.markdown-body .highlight {
  font-size: 15px;
}
.loading body:before {
  display: none;
}
#bar {
  z-index: 5;
  position: absolute;
  box-sizing: border-box;
  border-right: 10px solid #fff;
  border-left: 10px solid #fff;
  top: 0;
  left: 0;
  width: 100%;
  height: 3px;
  background: #3f4d56;
  transition: all 1s ease;
}
