  .loading,
  .error,
  .transition {
    height: 100%;
  }
  .loading body,
  .error body,
  .transition body {
    height: 100%;
  }
  #loader {
    text-align: center;
    position: fixed;
    z-index: 3;
    left: 0;
    top: 0;
    height: 100%;
    width: 100%;
    opacity: 0;
    visibility: hidden;
    -webkit-transition: all .3s ease;
    transition: all .3s ease;
    background-color: rgba(255, 255, 255, .95);
  }
  .loading #loader {
    opacity: 1;
    visibility: visible;
  }
  .loading #loader div {
    display: inline-block;
    width: 50px;
    height: 50px;
    vertical-align: middle;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    background-color: #fff;
    -webkit-box-shadow: 0 0 3px rgba(0, 0, 0, .15);
    box-shadow: 0 0 3px rgba(0, 0, 0, .15);
  }
  .loading #loader div:before {
    content: '';
    width: 26px;
    height: 26px;
    display: block;
    margin: 8px auto 0;
    -webkit-border-radius: 20px;
    border-radius: 20px;
    -webkit-animation: loading .5s linear forwards infinite;
    animation: loading .5s linear forwards infinite;
    border-top: 4px solid #0089fa;
    border-right: 4px solid #ff002b;
    border-bottom: 4px solid #ffa900;
    border-left: 4px solid #00a753;
  }
  .loading #loader:after {
    content: '';
    display: inline-block;
    height: 100%;
    vertical-align: middle;
  }
  @keyframes loading { 100% { transform: rotate(360deg) } }
  @-webkit-keyframes loading { 100% { -webkit-transform: rotate(360deg) } }
